"use strict";

// src/manager.ts
var import_addons = require("@storybook/addons");

// src/constants.ts
var ADDON_ID = "storybook/links";
var constants_default = {
  NAVIGATE: `${ADDON_ID}/navigate`,
  REQUEST: `${ADDON_ID}/request`,
  RECEIVE: `${ADDON_ID}/receive`
};

// src/manager.ts
import_addons.addons.register(ADDON_ID, (api) => {
  const channel = import_addons.addons.getChannel();
  channel.on(constants_default.REQUEST, ({ kind, name }) => {
    const id = api.storyId(kind, name);
    api.emit(constants_default.RECEIVE, id);
  });
});
